$(function() {
  'use strict';
  $(window).on('load', function() {
    $('body').addClass('loaded');
  }),
    $('body').scrollspy({ target: '.menu' });
  var e = $('.menu'),
    o = $('.toggle-btn'),
    s = $('section, footer, .toggle-btn');
  o.on('click', function(o) {
    e.hasClass('show-menu')
      ? e.removeClass('show-menu')
      : e.addClass('show-menu'),
      o.stopPropagation();
  }),
    o.on('click', function() {
      s.hasClass('push-content')
        ? s.removeClass('push-content')
        : s.addClass('push-content');
    }),
    o.on('click', function() {
      o.hasClass('toggle-close')
        ? o.removeClass('toggle-close')
        : o.addClass('toggle-close');
    });
  var a = $('html,body');
  $('.menu ul li a, .scroll-down').on('click', function(e) {
    a.animate({ scrollTop: $(this.hash).offset().top }, 700, 'easeInOutQuart'),
      e.preventDefault();
  }),
    $('#mix-container').mixItUp(),
    $('.lightbox').magnificPopup({
      type: 'inline',
      fixedContentPos: !1,
      removalDelay: 100,
      closeBtnInside: !0,
      preloader: !1,
      mainClass: 'mfp-fade'
    })
});
